(function(w, d) {
    function a() {
        var b = d.createElement("script");
        b.async = !0;
        b.src = "web/static/src/cust_flatsome/js/lazyload.1.0.5.min.js";
        var a = d.getElementsByTagName("script")[0];
        a.parentNode.insertBefore(b, a)
    }
    w.attachEvent ? w.attachEvent("onload", a) : w.addEventListener("load", a, !1)
})(window, document);

$(document).ready(function(){
    $(".owl-carousel").owlCarousel(
        {
            loop:true, margin:45, nav:false, autoplay:true, autoplayTimeout:3000, 
            responsive:{       
                0:{
                    items:1
                },
                768:{
                    items:3
                },
                1024:{
                    items:5
                }
            }
        }
    );
    $(".owl-prev, .owl-next").html("");

    $(".owl-carousel").on('mousewheel', '.owl-stage', function (e) {
        if (e.deltaY>0) {
            owl.trigger('next.owl');
        } else {
            owl.trigger('prev.owl');
        }
        e.preventDefault();
    });
});
